import React from 'react';
import './Navbar.css';
import HoverImage from "react-hover-image";
import normalUser from "../../assets/user.png";
import hoverUser from "../../assets/user-hover.png";
import normalWishlist from "../../assets/wishlist.png";
import hoverWishlist from "../../assets/wishlist-hover.png";
import normalCart from "../../assets/cart.png";
import hoverCart from "../../assets/cart-hover.png";

class Navbar extends React.Component {
    constructor(props) {
        super(props);
        this.hoverCategories = this.hoverCategories.bind(this);
    };
    hoverCategories() {
        const categoriesDropdown = document.querySelector('.categories-dropdown');
        const imgCategories = document.querySelector('.image-categories');
        const textCategories = document.querySelector('.text-categories');
        const arrowDown = document.querySelector('.image-arrow-down');
        categoriesDropdown.addEventListener('mouseover', () => {
            imgCategories.src = "data:image/png;base64,";
            textCategories.style.color = 'black';
            arrowDown.src = "data:image/png;base64,";
        });
    };
    normalCategories() {
        const categoriesDropdown = document.querySelector('.categories-dropdown');
        const imgCategories = document.querySelector('.image-categories');
        const textCategories = document.querySelector('.text-categories');
        const arrowDown = document.querySelector('.image-arrow-down');
        categoriesDropdown.addEventListener('mouseout', () => {
            imgCategories.src = "data:image/png;base64,";
            textCategories.style.color = 'white';
            arrowDown.src = "data:image/png;base64,";
        });
    };
    searchInput() {
        const searchBar = document.querySelector('.search');
        const searchIcon = document.querySelector('.image-search');
        searchBar.addEventListener('input', () => {
            searchIcon.src = "data:image/png;base64,";
        });
        if (searchBar.value === "") {
            searchIcon.src = searchIcon.src = "data:image/png;base64,";
        };
    };
    cancelClose() {
        const searchBar = document.querySelector('.search');
        const searchIcon = document.querySelector('.image-search');
        if (searchIcon.src === "data:image/png;base64,") {
            searchIcon.addEventListener('click', () => {
                searchBar.value = "";
                searchIcon.src = "data:image/png;base64,";
            });
        };
    };
    render() {
        return (
            <div className='navbar-body'>
                <div className='navbar'>
                    <div className='navbar-containers'>
                        <div className='navbar-brand'>
                            <a href='#pocketbookie'>
                                <img src={require('../../assets/PocketBookie.png')} alt='logo'></img>
                            </a>
                        </div>
                        <div className='navbar-categories-search'>
                            <div class="categories-dropdown" onMouseOver={this.hoverCategories} onMouseOut={this.normalCategories}>
                                <img src={require('../../assets/categories.png')} alt='categories' className='image-categories'></img>
                                <span className='text-categories'>Categories&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span>
                                <img src={require('../../assets/arrow-down.png')} alt='arrow-down' className='image-arrow-down'></img>
                            </div>
                            <div className='navbar-search'>
                                <input type="text" placeholder="Search Books" className='search' onInput={this.searchInput}></input>
                                <img src={require('../../assets/search.png')} alt='search' className='image-search' onClick={this.cancelClose} id='search'></img>
                            </div>
                        </div>
                        <div className='navbar-options'>
                            <a href='#login' className='nav-links'>
                                <HoverImage className='image-user' src={normalUser} hoverSrc={hoverUser} />
                            </a>
                            <div class="navbar-vl"></div>
                            <a href='#wishlist' className='nav-links'>
                                <HoverImage className='image-wishlist' src={normalWishlist} hoverSrc={hoverWishlist} />
                            </a>
                            <div class="navbar-vl"></div>
                            <a href='#cart'>
                                <HoverImage className='image-cart' src={normalCart} hoverSrc={hoverCart} />
                            </a>
                        </div>
                    </div>
                </div>
            </div>
        );
    };
};



export default Navbar;